function filterBrand(id) {        
        var parent = document.querySelector(".tab-content .row")
        while(parent.firstChild) {
            parent.firstChild.remove();
        }

        products(id,0)
}

function filterCategories(id) {        
    var parent = document.querySelector(".tab-content .row")
    while(parent.firstChild) {
        parent.firstChild.remove();
    }

    products(0,id)
}
