var cartList = new Object();

async function finalizar(){


    cartList = JSON.parse(window.localStorage.getItem('cart'))
    
    let produtos = new Array();

    for(item of Object.keys(cartList)) { 
        produtos.push({
            produto: cartList[item].id_produto,
            quantidade: cartList[item].count
        })
    }


    const data = {
        cliente: localStorage.getItem("id"),
        total: window.localStorage.getItem("total"),
        comprasProdutos: produtos
    }
    const response = await fetch('https://projeto-bd2.herokuapp.com/compras', {
        headers:{'Content-Type': 'application/json',},
        method: "POST",
        body: JSON.stringify(data)
    }).then(async (response) => {
        const data = await response.json()
        console.log(data)
    })
    
}
